const a=document.getElementById("menu"),n=document.getElementById("navbar");a?.addEventListener("click",()=>{n?.classList.toggle("hidden")});document.addEventListener("click",t=>{const e=t.target;!e.closest("#menu")&&!e.closest("#navbar")&&n?.classList.add("hidden")});const c=window.location.pathname,s=document.querySelectorAll("nav a");s.forEach(t=>{t.getAttribute("href")===c&&t.setAttribute("aria-current","page")});
